import React, { useState } from 'react';
import Box from './Box.js';
import NewBoxForm from './NewBoxForm';
import uuid from 'uuid/v4';

function BoxList() {
    const INITIAL_STATE = [
        { backgroundColor: 'black', width: '200px', height: '200px'}, 
        { backgroundColor: 'blue', width: '400px', height: '100px'},
    ]
    const [boxes, setBoxes] = useState(INITIAL_STATE);
    const addBox = (backgroundColor, width, height) => {
        setBoxes(boxes => [...boxes, {backgroundColor, width, height}])
    }
    return (
        <>
            <NewBoxForm addBox={addBox} />
            <div>
                {boxes.map(({backgroundColor, width, height }) => <Box backgroundColor={backgroundColor} width={width} height={height} />)}
            </div>
        </>
    );
}

export default BoxList;
